import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";

import './Expenses.css'
import {useState} from "react";

function Expenses(props) {

    const [filteredYear, setFilteredYear] = useState('');

    const handleFilterUpdate = (filteredYear) => {
        setFilteredYear(filteredYear);
    }

    return (
        <div>
            <ExpensesFilter selected={filteredYear} onUpdateFilter={handleFilterUpdate}/>
            <Card className='expenses'>
                {props.expenseItems.filter(expenseItem => expenseItem.date.year === filteredYear )
                    .map(expenseItem => <ExpenseItem
                    key={expenseItem.id}
                    title={expenseItem.title}
                    amount={expenseItem.amount}
                    date={expenseItem.date}
                />)}
            </Card>
        </div>
    );
}

export default Expenses;
